    }



    return (
        <div className='signbg'>
            <div className='signbg2'>

                <div className="form mt-5 ">
                    <span className='span text-warning'>Signup!</span><br />
                    <input type="text"
                        placeholder='Username'
                        className='username mt-5'
                        name='username'
                        onChange={handelfrom}
                        value={formData.username} /><br />

                    <input type="email"
                        placeholder='e-mail'
                        className='email mt-3'
                        name='email'
                        onChange={handelfrom}
                        value={formData.email} /><br />

                    <input type="password"
                        placeholder='Password'
                        className='password mt-3'
                        name='password'
                        onChange={handelfrom}
                        value={formData.password} /><br />

                    <input type="password"
                        placeholder='Confrim password'
                        className='con-password mt-3'
                        name='conPassword'
                        onChange={handelfrom}
                        value={formData.conPassword} /><br />

                    <div className='gender text-white mt-3'>
                        <input type="radio" name="gender" onChange={handelfrom} value={formData.gender} /><span className='gend me-5' >Male</span>
                        <input type="radio" name="gender" onChange={handelfrom} value={formData.gender} /><span className='gend me-5'>Female</span>
                    </div>
                    <div>
                        <button className='sigbtn text-white mt-5'><Link to={"/Login"} className='toLogin text-white'>Signup</Link></button>
                    </div>
                    <div className=' text-white mt-4'>
                        <span >Already have an account?<Link to={"/Login"} className='toLogin text-white'>Login</Link></span>
                    </div>
                </div>

            </div>

        </div>
    )
}

export default Signup
