import React, { useState } from 'react'
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min.js';
import signup from "../signuppage/signup.css"
import { Link } from 'react-router-dom';
import Login from '../Login/Login';

function Signup() {
    const [formData, setformData] = useState({
        username: "",
        email: "",
        password: "",
        conPassword: "",
        gender: "",
    })

    const [Errors, setErrors] = useState({
        username: "",
        email: "",
        password: "",
        conPassword: "",
        gender: "",
    })

    const handelfrom = (e) => {
        const { name, value } = e.target;
        setformData({
            ...formData,
            [name]: value,
        });
    };

    const validateForm = () => {
        let Errors = { username: "", email: "", password: "", conPassword: "", gender: "" };
        let isValid = true;
    

    if (!formData.username) {
        Errors.username = "Username is required";
        isValid = false;
    }

    if (!formData.email) {
        Errors.email = "Email is required";
        isValid = false;
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
        Errors.email = "Email address is invalid";
        isValid = false;
    }

    if (!formData.password) {
        Errors.password = "Password is required";
        isValid = false;
    } else if (formData.password.length < 6) {
        Errors.password = "Password must be at least 6 characters";
        isValid = false;
    }

    if (!formData.conPassword) {
        Errors.email = "Email is required";
        isValid = false;
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
        Errors.email = "Email address is invalid";
        isValid = false;
    }

    if (!formData.gender) {
        Errors.gender="gender is not select";
        isValid=false;
    }
    setErrors(Errors);
    return isValid;
}

const formSubmit = (e) => {
    e.preventDefault();
    if (validateForm()) {
      console.log("Form Submitted", formData);
      localStorage.getItem(setformData)
    }
  };



    return (
        <div className='signbg'>
            <div className='signbg2'>

                <div className="form mt-5  " onSubmit={formSubmit} >
                    <span className='span text-warning'>Signup!</span><br />
                    <input type="text"
                        placeholder='Username'
                        className='username mt-5'
                        name='username'
                        onChange={handelfrom}
                        value={formData.username} />
                        {Errors.username && <div style={{ color: "red" }}>{Errors.username}</div>}<br />

                    <input type="email"
                        placeholder='e-mail'
                        className='email mt-3'
                        name='email'
                        onChange={handelfrom}
                        value={formData.email} />
                        {Errors.email && <div style={{ color: "red" }}>{Errors.email}</div>}<br />

                    <input type="password"
                        placeholder='Password'
                        className='password mt-3'
                        name='password'
                        onChange={handelfrom}
                        value={formData.password} /><br />

                    <input type="password"
                        placeholder='Confrim password'
                        className='con-password mt-3'
                        name='conPassword'
                        onChange={handelfrom}
                        value={formData.conPassword} /><br />

                    <div className='gender text-white mt-3'>
                        <input type="radio" name="gender" onChange={handelfrom} value={formData.gender} /><span className='gend me-5' >Male</span>
                        <input type="radio" name="gender" onChange={handelfrom} value={formData.gender} /><span className='gend me-5'>Female</span>
                    </div>
                    <div>
                        <Link to={"/Login"} className='toLogin text-white'><button className='sigbtn text-white mt-5' type='submit'>Signup </button></Link>
                    </div>
                    <div className=' text-white mt-4'>
                        <span >Already have an account?<Link to={"/Login"} className='toLogin text-white'>Login</Link></span>
                    </div>
                </div>

            </div>

        </div>
    )
}

export default Signup
