import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min.js';
import '../Login/Login.css';
import { Link } from 'react-router-dom';
import Signup from '../signuppage/Signup';
import Forget from '../forgetpassword/Forget';


function Login() {
  const [account, setaccount] = useState({
    username: "",
    password: ""
  });

  const handelfrom = (e) => {
    const { name, value } = e.target;
    setaccount({
        ...account,
        [name]: value,
    });
  }
  
  function formSubmit(event) {
    event.preventDefault()
    if (account.user.username !== "" && formData.email !== "" && formData.password !== "") {
        localStorage.getItem("regname",formData.username)
        localStorage.getItem("regpassword",formData.password)
        console.log("Submited successfully")
    } else {
        alert("Please fill all fields")
    }}
  return (
    <div className="lognbg">

      <div className="logbg ">

        <div className='login mt-5'>
          <span className='title text-warning'>Login</span>

          <form onClick={formSubmit} className='formdata mt-5'>

            <input type="text"
              placeholder='Username'
              name='username'
              className='user'
              value={account.username}
              onChange={handelfrom} />
            <br />

            <input type="password"
              placeholder='Password'
              name='password'
              className='pass m-4'
              value={account.password} 
              onChange={handelfrom}/>
            <br />

            <Link to={"/Forget"} className='forget text-white'>Forget password?</Link>
            <br />
            <span className='remember text-white'><input type="checkbox" /> Remember me</span><br />

            <Link to={"/shop-pg"}>
              <button className='logbtn mt-5' type='submit'>Login</button><br /></Link>
            <div className='noacc text-white mt-4'>
              <span >Have no account ? <Link to={"/Signup"} className='signpg text-white'>signup!</Link></span>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}

export default Login;
